CREATE TABLE user_role (id BIGINT IDENTITY NOT NULL PRIMARY KEY, name character varying(30) NOT NULL); CREATE TABLE user_account (id BIGINT IDENTITY NOT NULL PRIMARY KEY, name character varying(20) NOT NULL, surname character varying(20) NOT NULL, username character varying(30) NOT NULL, password character varying(30) NOT NULL); CREATE TABLE author (id BIGINT IDENTITY NOT NULL PRIMARY KEY, name character varying(30) NOT NULL, surname character varying(30) NOT NULL); CREATE TABLE news (id BIGINT IDENTITY NOT NULL PRIMARY KEY, title character varying(30) NOT NULL, short_text character varying(100) NOT NULL, full_text character varying(2000) NOT NULL, creation_date date NOT NULL, modification_date date NOT NULL ); CREATE TABLE tag ( id BIGINT IDENTITY NOT NULL PRIMARY KEY, name character varying(30) NOT NULL ); CREATE TABLE news_author ( author_id BIGINT NOT NULL, news_id BIGINT NOT NULL, FOREIGN KEY (author_id) REFERENCES author (id) ON DELETE CASCADE, FOREIGN KEY (news_id) REFERENCES news (id) ON DELETE CASCADE); CREATE TABLE news_tag (news_id BIGINT NOT NULL, tag_id BIGINT NOT NULL, FOREIGN KEY (tag_id) REFERENCES tag (id) ON DELETE CASCADE, FOREIGN KEY (news_id) REFERENCES news (id) ON DELETE CASCADE); CREATE TABLE users_and_roles (user_id BIGINT NOT NULL, role_id BIGINT NOT NULL, FOREIGN KEY (user_id) REFERENCES user_account (id) ON DELETE CASCADE, FOREIGN KEY (role_id) REFERENCES user_role (id) ON DELETE CASCADE);